// client/src/pages/RequestExchangePage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useNavigate, useLocation, Link } from 'react-router-dom'; // Added Link for consistency
import { useAuth } from '../contexts/AuthContext';

function RequestExchangePage() {
    const { token, user } = useAuth(); // User is not used here but kept for consistency if needed
    const navigate = useNavigate();
    const location = useLocation();

    const [requestedBookId, setRequestedBookId] = useState('');
    const [requestedBookTitle, setRequestedBookTitle] = useState('');
    const [ownerId, setOwnerId] = useState(''); // Owner of the requested book

    const [myBooks, setMyBooks] = useState([]);
    const [offeredBookId, setOfferedBookId] = useState('');
    const [requestMessage, setRequestMessage] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const queryParams = new URLSearchParams(location.search);
        const id = queryParams.get('requestedBookId');
        const title = queryParams.get('requestedBookTitle');
        const owner = queryParams.get('ownerId');

        if (id && title && owner) {
            setRequestedBookId(id);
            setRequestedBookTitle(decodeURIComponent(title));
            setOwnerId(owner);
        } else {
            toast.error('Missing book information for exchange request.');
            navigate('/books');
            return;
        }

        const fetchMyBooks = async () => {
            try {
                const config = { headers: { Authorization: `Bearer ${token}` } };
                const { data } = await axios.get('/api/books/mybooks', config);
                // Filter to only show 'Available' books from user's own listings
                setMyBooks(data.filter(book => book.availabilityStatus === 'Available'));
                setLoading(false);
            } catch (err) {
                console.error('Error fetching my books:', err);
                setError('Failed to load your books for offering.');
                toast.error('Failed to load your books.');
                setLoading(false);
            }
        };

        if (token) {
            fetchMyBooks();
        } else {
            setLoading(false);
            toast.warn('Please log in to proceed with the exchange request.');
            navigate('/login');
        }
    }, [location.search, navigate, token]); // Added token to dependencies as it's used in fetchMyBooks

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const requestData = {
                requestedBookId: requestedBookId,
                offeredBookId: offeredBookId || null, // Send null if no book is offered
                requestMessage: requestMessage,
            };
            await axios.post('/api/exchanges', requestData, config);
            toast.success('Exchange request sent successfully!');
            navigate('/exchanges'); // Redirect to my exchanges page
        } catch (error) {
            const errorMessage = error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
            toast.error(`Failed to send request: ${errorMessage}`);
            console.error('Error sending exchange request:', error);
        }
    };

    if (loading) {
        return <div style={{ textAlign: 'center', padding: '16px' }}>Loading your books...</div>; // Added style for consistency
    }

    if (error) {
        return <div style={{ color: 'red', textAlign: 'center', padding: '16px' }}>Error: {error}</div>; // Added style for consistency
    }

    return (
        <div style={{
            maxWidth: '500px', // max-w-md (approx from previous AddBookPage)
            margin: '40px auto', // mx-auto mt-10
            padding: '20px', // p-6
            backgroundColor: 'white', // bg-white
            borderRadius: '8px', // rounded-lg
            boxShadow: '0 0 10px rgba(0,0,0,0.05)' // shadow-md (approx)
        }}>
            <h1 style={{ fontSize: '2em', fontWeight: 'bold', marginBottom: '24px', textAlign: 'center' }}>Request Exchange for: "{requestedBookTitle}"</h1>
            <form onSubmit={handleSubmit} style={{
                display: 'grid',
                gap: '16px', // space-y-4 (approx)
            }}>
                <div>
                    <label htmlFor="offeredBook" style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>Offer your book (Optional):</label>
                    <select
                        id="offeredBook"
                        name="offeredBook"
                        value={offeredBookId}
                        onChange={(e) => setOfferedBookId(e.target.value)}
                        className="form-input" // Use global form-input class
                    >
                        <option value="">-- Select a book to offer --</option>
                        {myBooks.map((book) => (
                            <option key={book._id} value={book._id}>
                                {book.title} by {book.author} ({book.condition})
                            </option>
                        ))}
                    </select>
                    {myBooks.length === 0 && <p style={{fontSize: '0.9em', color: '#666', marginTop: '8px'}}>You don't have any available books to offer. <Link to="/books/add" style={{ color: '#007bff', textDecoration: 'underline' }}>Add one now!</Link></p>}
                </div>
                <div>
                    <label htmlFor="requestMessage" style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>Your Message:</label>
                    <textarea
                        id="requestMessage"
                        name="requestMessage"
                        value={requestMessage}
                        onChange={(e) => setRequestMessage(e.target.value)}
                        rows="5"
                        placeholder="Tell the owner why you're interested in their book and suggest meetup/shipping details."
                        className="form-input" // Use global form-input class
                        required
                    ></textarea>
                </div>
                <button
                    type="submit"
                    className="form-button" // Use global form-button class
                    style={{ // Override specific button color and hover
                        backgroundColor: '#007bff', // standard blue
                        padding: '10px 20px', // Match previous style
                        fontSize: '1em' // Match previous style
                    }}
                    onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#0056b3'}
                    onMouseOut={(e) => e.currentTarget.style.backgroundColor = '#007bff'}
                >
                    Send Request
                </button>
            </form>
        </div>
    );
}

export default RequestExchangePage;
