// client/src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import { useAuth } from './contexts/AuthContext'; // Import useAuth hook
import HomePage from './pages/HomePage';
import RegisterPage from './pages/RegisterPage';
import LoginPage from './pages/LoginPage';
import BookListPage from './pages/BookListPage'; // Will create this
import AddBookPage from './pages/AddBookPage'; // Will create this
import MyBooksPage from './pages/MyBooksPage'; // Will create this
import ExchangeRequestsPage from './pages/ExchangeRequestsPage'; // Will create this
import ExchangeDetailsPage from './pages/ExchangeDetailsPage'; // Will create this
import ProtectedRoute from './components/ProtectedRoute'; // Protected route component

function App() {
    const { user, logout, isAuthenticated } = useAuth();
    const navigate = useNavigate(); // For programmatic navigation after logout

    const handleLogout = () => {
        logout();
        navigate('/login'); // Redirect to login page after logout
    };

    return (
        <div>
            <Header /> {/* We'll create a Header component */}
            <main style={{ padding: '20px' }}> {/* Basic styling */}
                <Routes>
                    <Route path="/" element={<HomePage />} />
                    <Route path="/register" element={<RegisterPage />} />
                    <Route path="/login" element={<LoginPage />} />

                    {/* Public routes for Browse books (even unauthenticated users can see them) */}
                    <Route path="/books" element={<BookListPage />} />
                    {/* More specific public routes could go here like /books/:id */}

                    {/* Protected Routes */}
                    <Route
                        path="/books/add"
                        element={
                            <ProtectedRoute>
                                <AddBookPage />
                            </ProtectedRoute>
                        }
                    />
                    <Route
                        path="/my-books"
                        element={
                            <ProtectedRoute>
                                <MyBooksPage />
                            </ProtectedRoute>
                        }
                    />
                    <Route
                        path="/exchanges"
                        element={
                            <ProtectedRoute>
                                <ExchangeRequestsPage />
                            </ProtectedRoute>
                        }
                    />
                     <Route
                        path="/exchanges/:id"
                        element={
                            <ProtectedRoute>
                                <ExchangeDetailsPage />
                            </ProtectedRoute>
                        }
                    />
                    {/* Fallback for undefined routes */}
                    <Route path="*" element={<h1>404 Not Found</h1>} />
                </Routes>
            </main>
            <Footer /> {/* We'll create a Footer component */}
        </div>
    );
}

// Header Component (Basic - to be enhanced)
function Header() {
    const { user, isAuthenticated, logout } = useAuth();
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    return (
        <header style={{ padding: '10px 20px', background: '#333', color: '#fff', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <nav>
                <Link to="/" style={{ color: '#fff', marginRight: '15px' }}>Home</Link>
                <Link to="/books" style={{ color: '#fff', marginRight: '15px' }}>Browse Books</Link>
                {isAuthenticated && (
                    <>
                        <Link to="/books/add" style={{ color: '#fff', marginRight: '15px' }}>Add Book</Link>
                        <Link to="/my-books" style={{ color: '#fff', marginRight: '15px' }}>My Books</Link>
                        <Link to="/exchanges" style={{ color: '#fff', marginRight: '15px' }}>My Exchanges</Link>
                    </>
                )}
            </nav>
            <div>
                {isAuthenticated ? (
                    <>
                        <span style={{ marginRight: '15px' }}>Welcome, {user.username}!</span>
                        <button onClick={handleLogout} style={{ background: 'none', border: '1px solid #fff', color: '#fff', padding: '5px 10px', cursor: 'pointer' }}>Logout</button>
                    </>
                ) : (
                    <>
                        <Link to="/register" style={{ color: '#fff', marginRight: '15px' }}>Register</Link>
                        <Link to="/login" style={{ color: '#fff' }}>Login</Link>
                    </>
                )}
            </div>
        </header>
    );
}

// Basic Footer Component
function Footer() {
    return (
        <footer style={{ padding: '10px 20px', background: '#333', color: '#fff', textAlign: 'center', marginTop: 'auto' }}>
            <p>&copy; {new Date().getFullYear()} Book Exchange Platform</p>
        </footer>
    );
}

export default App;
