// client/src/pages/BookListPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';

function BookListPage() {
    const { user, isAuthenticated } = useAuth();
    const navigate = useNavigate();

    const [books, setBooks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchBooks = async () => {
            try {
                setLoading(true);
                const { data } = await axios.get('/api/books');
                setBooks(data);
                setLoading(false);
            } catch (err) {
                console.error('Error fetching books:', err);
                setError('Failed to load books. Please try again later.');
                toast.error('Failed to load books.');
                setLoading(false);
            }
        };

        fetchBooks();
    }, []);

    const handleRequestExchange = (requestedBookId, ownerId, requestedBookTitle) => {
        if (!isAuthenticated) {
            toast.warn('Please log in to request an exchange.');
            navigate('/login');
            return;
        }
        if (user._id === ownerId) {
            toast.info('You cannot request an exchange for your own book.');
            return;
        }
        navigate(`/request-exchange?requestedBookId=${requestedBookId}&ownerId=${ownerId}&requestedBookTitle=${encodeURIComponent(requestedBookTitle)}`);
    };

    if (loading) {
        return <div style={{ textAlign: 'center', padding: '16px' }}>Loading books...</div>; // text-center py-4
    }

    if (error) {
        return <div style={{ color: 'red', textAlign: 'center', padding: '16px' }}>Error: {error}</div>; // text-red-500 text-center py-4
    }

    return (
        <div>
            <h1 style={{ fontSize: '2.25em', fontWeight: 'bold', textAlign: 'center', marginBottom: '32px' }}>Available Books</h1> {/* text-3xl font-bold text-center mb-8 */}
            {books.length === 0 ? (
                <p style={{ textAlign: 'center', color: '#6b7280' }}>No books are available for exchange at the moment. Be the first to add one!</p> // text-center text-gray-600
            ) : (
                <div style={{
                    display: 'grid',
                    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', // grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4
                    gap: '24px' // gap-6
                }}>
                    {books.map((book) => (
                        <div key={book._id} className="book-card"> {/* Using a common class for book cards */}
                            <img
                                src={book.imageUrl}
                                alt={book.title}
                                style={{
                                    width: '100%', // w-full
                                    height: '192px', // h-48 (12rem * 16px/rem = 192px)
                                    objectFit: 'cover', // object-cover
                                }}
                                onError={(e) => { e.target.onerror = null; e.target.src = 'https://via.placeholder.com/150'; }}
                            />
                            <div style={{ padding: '16px', flexGrow: 1 }}> {/* p-4 flex-grow */}
                                <h3 style={{ fontSize: '1.25em', fontWeight: 'semibold', marginBottom: '8px' }}>{book.title}</h3> {/* text-xl font-semibold mb-2 */}
                                <p style={{ color: '#4b5563' }}><strong>Author:</strong> {book.author}</p> {/* text-gray-700 */}
                                <p style={{ color: '#4b5563' }}><strong>Genre:</strong> {book.genre || 'N/A'}</p> {/* text-gray-700 */}
                                <p style={{ color: '#4b5563' }}><strong>Condition:</strong> <span style={{ fontWeight: '500', color: '#2563eb' }}>{book.condition}</span></p> {/* text-gray-700 font-medium text-blue-600 */}
                                <p style={{ color: '#4b5563' }}><strong>Owner:</strong> {book.owner ? book.owner.username : 'Unknown'}</p> {/* text-gray-700 */}
                                <p style={{ color: '#4b5563', fontSize: '0.875em', marginTop: '8px', overflow: 'hidden', display: '-webkit-box', WebkitLineClamp: 3, WebkitBoxOrient: 'vertical' }}>{book.description}</p> {/* text-gray-600 text-sm mt-2 line-clamp-3 */}
                            </div>
                            {isAuthenticated && user && book.owner && user._id !== book.owner._id && (
                                <div style={{ padding: '16px', borderTop: '1px solid #e5e7eb' }}> {/* p-4 border-t border-gray-200 */}
                                    <button
                                        onClick={() => handleRequestExchange(book._id, book.owner._id, book.title)}
                                        className="form-button" // Reusing the general form button style
                                        style={{ width: '100%', padding: '8px 15px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }} // Override specific button styles
                                        onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#0056b3'}
                                        onMouseOut={(e) => e.currentTarget.style.backgroundColor = '#007bff'}
                                    >
                                        Request Exchange
                                    </button>
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default BookListPage;
