// client/src/pages/MyBooksPage.js
import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';
import { Link } from 'react-router-dom'; // For edit links later

function MyBooksPage() {
    const { token } = useAuth();
    const [myBooks, setMyBooks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchMyBooks = useCallback(async () => {
        if (!token) return;
        try {
            setLoading(true);
            const config = {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            };
            const { data } = await axios.get('/api/books/mybooks', config);
            setMyBooks(data);
            setLoading(false);
        } catch (err) {
            console.error('Error fetching my books:', err);
            setError('Failed to load your books. Please try again later.');
            toast.error('Failed to load your books.');
            setLoading(false);
        }
    }, [token]);

    useEffect(() => {
        fetchMyBooks();
    }, [fetchMyBooks]);

    const handleDelete = async (bookId) => {
        if (window.confirm('Are you sure you want to delete this book?')) {
            try {
                const config = {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                };
                await axios.delete(`/api/books/${bookId}`, config);
                toast.success('Book deleted successfully!');
                fetchMyBooks();
            } catch (error) {
                const errorMessage = error.response && error.response.data.message
                    ? error.response.data.message
                    : error.message;
                toast.error(`Failed to delete book: ${errorMessage}`);
                console.error('Error deleting book:', error);
            }
        }
    };

    if (loading) {
        return <div style={{ textAlign: 'center', padding: '16px' }}>Loading your books...</div>;
    }

    if (error) {
        return <div style={{ color: 'red', textAlign: 'center', padding: '16px' }}>Error: {error}</div>;
    }

    const getStatusColor = (status) => {
        switch (status) {
            case 'Available': return 'green';
            case 'Pending Exchange': return 'orange';
            case 'Exchanged': return 'red';
            default: return 'black'; // Fallback
        }
    };

    return (
        <div>
            <h1 style={{ fontSize: '2.25em', fontWeight: 'bold', textAlign: 'center', marginBottom: '32px' }}>My Listed Books</h1>
            {myBooks.length === 0 ? (
                <p style={{ textAlign: 'center', color: '#6b7280' }}>You haven't listed any books yet. <Link to="/books/add" style={{ color: '#2563eb', textDecoration: 'underline' }}>Add your first book!</Link></p>
            ) : (
                <div style={{
                    display: 'grid',
                    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', // grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4
                    gap: '24px' // gap-6
                }}>
                    {myBooks.map((book) => (
                        <div key={book._id} className="book-card"> {/* Reusing the common book-card class */}
                            <img
                                src={book.imageUrl}
                                alt={book.title}
                                style={{
                                    width: '100%',
                                    height: '192px', // h-48
                                    objectFit: 'cover',
                                }}
                                onError={(e) => { e.target.onerror = null; e.target.src = 'https://via.placeholder.com/150'; }}
                            />
                            <div style={{ padding: '16px', flexGrow: 1 }}> {/* p-4 flex-grow */}
                                <h3 style={{ fontSize: '1.25em', fontWeight: 'semibold', marginBottom: '8px' }}>{book.title}</h3> {/* text-xl font-semibold mb-2 */}
                                <p style={{ color: '#4b5563' }}><strong>Author:</strong> {book.author}</p> {/* text-gray-700 */}
                                <p style={{ color: '#4b5563' }}><strong>Condition:</strong> <span style={{ fontWeight: '500', color: '#2563eb' }}>{book.condition}</span></p> {/* text-gray-700 font-medium text-blue-600 */}
                                <p style={{ color: '#4b5563' }}><strong>Status:</strong> <span style={{ fontWeight: '500', color: getStatusColor(book.availabilityStatus) }}>{book.availabilityStatus}</span></p> {/* text-gray-700 font-medium text-color-based-on-status */}
                                <p style={{ color: '#4b5563', fontSize: '0.875em', marginTop: '8px', overflow: 'hidden', display: '-webkit-box', WebkitLineClamp: 3, WebkitBoxOrient: 'vertical' }}>{book.description}</p> {/* text-gray-600 text-sm mt-2 line-clamp-3 */}
                            </div>
                            <div style={{ padding: '16px', borderTop: '1px solid #e5e7eb', display: 'flex', justifyContent: 'flex-end', gap: '12px' }}> {/* p-4 border-t border-gray-200 flex justify-end gap-3 */}
                                {/* <Link to={`/my-books/edit/${book._id}`} className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline transition duration-150 ease-in-out">Edit</Link> */}
                                <button
                                    onClick={() => handleDelete(book._id)}
                                    className="form-button" // Reuse general button style
                                    style={{ backgroundColor: 'red', width: 'auto' }} // Override specific color and width
                                    onMouseOver={(e) => e.currentTarget.style.backgroundColor = 'darkred'}
                                    onMouseOut={(e) => e.currentTarget.style.backgroundColor = 'red'}
                                >
                                    Delete
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default MyBooksPage;
