// frontend/src/pages/BookListPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import './BookList.css';
import '../App.css';
import '../components/Form.css';
import placeholderImage from '../assests/'; // Import placeholder

// NEW: Import the useDebounce hook
import useDebounce from '../hooks/useDebounce'; // Assuming hooks folder is sibling to pages

function BookListPage() {
    const { user, isAuthenticated } = useAuth();
    const navigate = useNavigate();

    const [books, setBooks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // State for search and filters
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedGenre, setSelectedGenre] = useState('All');
    const [selectedCondition, setSelectedCondition] = useState('All');

    // NEW: Debounced search term
    const debouncedSearchTerm = useDebounce(searchTerm, 500); // Debounce by 500ms

    const genres = [
        'All', 'Fiction', 'Non-Fiction', 'Fantasy', 'Science Fiction',
        'Mystery', 'Thriller', 'Biography', 'History', 'Self-Help',
        'Childrens', 'Education', 'Romance', 'Horror', 'Poetry',
        'Travel', 'Cookbook'
    ];
    const conditions = ['All', 'New', 'Like New', 'Good', 'Fair', 'Worn'];

    useEffect(() => {
        const fetchBooks = async () => {
            try {
                setLoading(true);
                const params = {};
                // Use debouncedSearchTerm here
                if (debouncedSearchTerm) { // Only add if not empty
                    params.keyword = debouncedSearchTerm;
                }
                if (selectedGenre !== 'All') {
                    params.genre = selectedGenre;
                }
                if (selectedCondition !== 'All') {
                    params.condition = selectedCondition;
                }

                console.log("Fetching books with params:", params);

                const { data } = await axios.get('/api/books', { params });
                setBooks(data);
                setLoading(false);
            } catch (err) {
                console.error('Error fetching books:', err);
                setError('Failed to load books. Please try again later.');
                toast.error('Failed to load books.');
                setLoading(false);
            }
        };

        // The effect now depends on debouncedSearchTerm, selectedGenre, selectedCondition
        // It will only re-run when debouncedSearchTerm *actually changes* after the delay,
        // or when genre/condition filters change immediately.
        fetchBooks();
    }, [debouncedSearchTerm, selectedGenre, selectedCondition]); // Dependencies: now includes debouncedSearchTerm

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value); // This still updates immediately
    };

    const handleGenreChange = (e) => {
        setSelectedGenre(e.target.value);
    };

    const handleConditionChange = (e) => {
        setSelectedCondition(e.target.value);
    };

    const handleClearFilters = () => {
        setSearchTerm(''); // This will reset debouncedSearchTerm too eventually
        setSelectedGenre('All');
        setSelectedCondition('All');
    };

    const handleRequestExchange = (requestedBookId, ownerId, requestedBookTitle) => {
        if (!isAuthenticated) {
            toast.warn('Please log in to request an exchange.');
            navigate('/login');
            return;
        }
        if (user._id === ownerId) {
            toast.info('You cannot request an exchange for your own book.');
            return;
        }
        navigate(`/request-exchange?requestedBookId=${requestedBookId}&ownerId=${ownerId}&requestedBookTitle=${encodeURIComponent(requestedBookTitle)}`);
    };

    if (loading) {
        return <div className="status-message">Loading books...</div>;
    }

    if (error) {
        return <div className="status-message error-message">Error: {error}</div>;
    }

    return (
        <div>
            <h1 className="page-heading">Available Books</h1>

            {/* Search and Filter Section */}
            <div className="search-filter-section">
                <div className="filter-group">
                    <label htmlFor="search">Search by Keyword:</label>
                    <input
                        type="text"
                        id="search"
                        value={searchTerm} // Input value still tracks immediate searchTerm
                        onChange={handleSearchChange}
                        placeholder="Title, author, description..."
                        className="form-input"
                    />
                </div>
                <div className="filter-group small">
                    <label htmlFor="genre-filter">Genre:</label>
                    <select
                        id="genre-filter"
                        value={selectedGenre}
                        onChange={handleGenreChange}
                        className="form-input"
                    >
                        {genres.map(genre => <option key={genre} value={genre}>{genre}</option>)}
                    </select>
                </div>
                <div className="filter-group small">
                    <label htmlFor="condition-filter">Condition:</label>
                    <select
                        id="condition-filter"
                        value={selectedCondition}
                        onChange={handleConditionChange}
                        className="form-input"
                    >
                        {conditions.map(condition => <option key={condition} value={condition}>{condition}</option>)}
                    </select>
                </div>
                <button
                    onClick={handleClearFilters}
                    className="clear-filters-button"
                >
                    Clear Filters
                </button>
            </div>


            {books.length === 0 ? (
                <p className="status-message info-message">No books match your criteria. Try adjusting your search/filters!</p>
            ) : (
                <div className="books-grid">
                    {books.map((book) => (
                        <div key={book._id} className="book-card">
                            <img
                                src={book.imageUrl}
                                alt={book.title}
                                onError={(e) => { e.target.onerror = null; e.target.src = placeholderImage; }}
                            />
                            <div className="book-card-info">
                                <h3>{book.title}</h3>
                                <p><strong>Author:</strong> {book.author}</p>
                                <p><strong>Genre:</strong> {book.genre || 'N/A'}</p>
                                <p><strong>Condition:</strong> <span className="condition-text">{book.condition}</span></p>
                                <p><strong>Owner:</strong> {book.owner ? book.owner.username : 'Unknown'}</p>
                                <p className="book-card-description line-clamp-3">{book.description}</p>
                            </div>
                            {isAuthenticated && user && book.owner && user._id !== book.owner._id && (
                                <div className="book-card-actions">
                                    <button
                                        onClick={() => handleRequestExchange(book._id, book.owner._id, book.title)}
                                        className="request-exchange-button"
                                    >
                                        Request Exchange
                                    </button>
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default BookListPage;
