// client/src/App.js
// ... (imports)
import RequestExchangePage from './pages/RequestExchangePage'; // <-- NEW IMPORT

function App() {
    return (
        <div>
            <Header />
            <main style={{ padding: '20px' }}>
                <Routes>
                    <Route path="/" element={<HomePage />} />
                    <Route path="/register" element={<RegisterPage />} />
                    <Route path="/login" element={<LoginPage />} />

                    <Route path="/books" element={<BookListPage />} />

                    {/* Protected Routes */}
                    <Route
                        path="/books/add"
                        element={ <ProtectedRoute><AddBookPage /></ProtectedRoute> }
                    />
                    <Route
                        path="/my-books"
                        element={ <ProtectedRoute><MyBooksPage /></ProtectedRoute> }
                    />
                    <Route
                        path="/request-exchange" // <-- NEW ROUTE for initiating requests
                        element={ <ProtectedRoute><RequestExchangePage /></ProtectedRoute> }
                    />
                    <Route
                        path="/exchanges"
                        element={ <ProtectedRoute><ExchangeRequestsPage /></ProtectedRoute> }
                    />
                     <Route
                        path="/exchanges/:id"
                        element={ <ProtectedRoute><ExchangeDetailsPage /></ProtectedRoute> }
                    />
                    <Route path="*" element={<h1>404 Not Found</h1>} />
                </Routes>
            </main>
            <Footer />
        </div>
    );
}
// ... (Header and Footer functions - ensure they are in their own files now as planned)
export default App;
