// frontend/src/components/Header.js
import React, { useState, useEffect, useCallback, useRef } from 'react'; // Import useRef for click outside
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useSocket } from '../contexts/SocketContext';
import axios from 'axios';
import './Header.css'; // Assuming this for Header styles

function Header() {
    const { user, isAuthenticated, logout, token } = useAuth();
    const { socket } = useSocket(); // isConnected is not directly used here but can be
    const navigate = useNavigate();
    const [unreadCount, setUnreadCount] = useState(0);
    const [dropdownOpen, setDropdownOpen] = useState(false); // <--- NEW STATE for dropdown visibility
    const dropdownRef = useRef(null); // <--- NEW REF for dropdown element

    // Function to fetch unread notification count
    const fetchUnreadCount = useCallback(async () => {
        if (!token || !isAuthenticated) {
            setUnreadCount(0);
            return;
        }
        try {
            const config = {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            };
            const { data } = await axios.get('/api/notifications/unread-count', config);
            setUnreadCount(data.count);
        } catch (error) {
            console.error('Error fetching unread notification count:', error);
            // Optionally, toast.error('Failed to fetch unread notifications');
        }
    }, [token, isAuthenticated]);

    useEffect(() => {
        // Fetch count on component mount and when auth state changes
        fetchUnreadCount();

        // Listen for newNotification events from socket to update count
        if (socket) {
            const handleNewNotification = () => {
                fetchUnreadCount(); // Re-fetch the count when a new notification arrives
            };
            socket.on('newNotification', handleNewNotification);

            // Clean up the event listener
            return () => {
                socket.off('newNotification', handleNewNotification);
            };
        }
    }, [socket, fetchUnreadCount]);

    // <--- NEW: Handle click outside to close dropdown ---
    useEffect(() => {
        function handleClickOutside(event) {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setDropdownOpen(false);
            }
        }
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, [dropdownRef]);

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    const toggleDropdown = () => { // <--- NEW: Function to toggle dropdown
        setDropdownOpen(prev => !prev);
    };

    return (
        <header className="header">
            <nav className="main-nav"> {/* Added class for main navigation */}
                <Link to="/" className="nav-link">Home</Link>
                <Link to="/books" className="nav-link">Browse Books</Link>
                {/* Other public links if any */}
            </nav>

            <div className="header-auth-section">
                {isAuthenticated ? (
                    <div className="user-menu-container" ref={dropdownRef}> {/* <--- NEW Container for dropdown */}
                        <button onClick={toggleDropdown} className="user-menu-toggle"> {/* <--- NEW Toggle Button */}
                            {/* You can replace this with an actual user profile picture if available */}
                            {user.profilePictureUrl ? (
                                <img src={user.profilePictureUrl} alt={user.username} className="user-avatar-header" onError={(e) => { e.target.onerror = null; e.target.src = 'https://via.placeholder.com/30'; }} />
                            ) : (
                                <i className="fas fa-user-circle"></i> // Placeholder icon if no image
                            )}
                            <span>Welcome, {user.username}!</span>
                            <i className={`fas fa-chevron-down dropdown-arrow ${dropdownOpen ? 'open' : ''}`}></i> {/* Arrow icon */}
                        </button>

                        {dropdownOpen && (
                            <div className="dropdown-menu"> {/* <--- NEW Dropdown Menu */}
                                <Link to="/profile" onClick={() => setDropdownOpen(false)} className="dropdown-item">My Profile</Link>
                                <Link to="/my-books" onClick={() => setDropdownOpen(false)} className="dropdown-item">My Books</Link>
                                <Link to="/my-exchanges" onClick={() => setDropdownOpen(false)} className="dropdown-item">My Exchanges</Link>
                                <Link to="/notifications" onClick={() => setDropdownOpen(false)} className="dropdown-item notifications-dropdown-item">
                                    Notifications
                                    {unreadCount > 0 && <span className="notification-badge">{unreadCount}</span>}
                                </Link>
                                {/* Add a wishlist link here, will create "My Wishlist" page later */}
                                <Link to="/wishlist" onClick={() => setDropdownOpen(false)} className="dropdown-item">My Wishlist</Link>

                                <div className="dropdown-divider"></div> {/* Separator */}
                                <button onClick={handleLogout} className="dropdown-item logout-button">Logout</button>
                            </div>
                        )}
                    </div>
                ) : (
                    <div className="header-login-register">
                        <Link to="/register" className="btn btn-register">Register</Link>
                        <Link to="/login" className="btn btn-login">Login</Link>
                    </div>
                )}
            </div>
        </header>
    );
}

export default Header;
