// client/src/pages/ExchangeDetailsPage.js
import React, { useEffect, useState, useCallback, useRef } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

function ExchangeDetailsPage() {
    const { id: exchangeRequestId } = useParams();
    const { token, user } = useAuth();
    const navigate = useNavigate();

    const [exchange, setExchange] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessageContent, setNewMessageContent] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const messagesEndRef = useRef(null);

    const fetchExchangeDetails = useCallback(async () => {
        if (!token || !exchangeRequestId) return;
        try {
            setLoading(true);
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const { data } = await axios.get(`/api/exchanges/${exchangeRequestId}`, config);
            setExchange(data);
            setLoading(false);
        } catch (err) {
            console.error('Error fetching exchange details:', err);
            const errorMessage = err.response && err.response.data.message
                ? err.response.data.message
                : err.message;
            setError(`Failed to load exchange details: ${errorMessage}`);
            toast.error(`Failed to load exchange details: ${errorMessage}`);
            setLoading(false);
            if (err.response && (err.response.status === 403 || err.response.status === 404)) {
                navigate('/exchanges');
            }
        }
    }, [exchangeRequestId, token, navigate]);

    const fetchMessages = useCallback(async () => {
        if (!token || !exchangeRequestId) return;
        try {
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const { data } = await axios.get(`/api/messages/${exchangeRequestId}`, config);
            setMessages(data);
        } catch (err) {
            console.error('Error fetching messages:', err);
            toast.error('Failed to load messages.');
        }
    }, [exchangeRequestId, token]);

    useEffect(() => {
        fetchExchangeDetails();
        fetchMessages();
        const interval = setInterval(fetchMessages, 5000);
        return () => clearInterval(interval);
    }, [fetchExchangeDetails, fetchMessages]);

    useEffect(() => {
        if (messagesEndRef.current) {
            messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    }, [messages]);


    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (!newMessageContent.trim()) {
            toast.warn('Message cannot be empty.');
            return;
        }
        if (!exchange || !user) {
            toast.error('Cannot send message: Exchange or user data missing.');
            return;
        }

        const receiverId = exchange.requester._id === user._id ? exchange.owner._id : exchange.requester._id;

        try {
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const messageData = {
                receiverId,
                exchangeRequestId,
                content: newMessageContent,
            };
            await axios.post('/api/messages', messageData, config);
            setNewMessageContent('');
            toast.success('Message sent!');
            fetchMessages();
        } catch (error) {
            const errorMessage = error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
            toast.error(`Failed to send message: ${errorMessage}`);
            console.error('Error sending message:', error);
        }
    };

    const updateRequestStatus = async (newStatus) => {
        if (!window.confirm(`Are you sure you want to ${newStatus.toLowerCase()} this request?`)) {
            return;
        }
        try {
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const { data } = await axios.put(`/api/exchanges/${exchangeRequestId}/status`, { status: newStatus }, config);
            setExchange(data);
            toast.success(`Request ${newStatus.toLowerCase()} successfully!`);
        } catch (error) {
            const errorMessage = error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
            toast.error(`Failed to update request: ${errorMessage}`);
            console.error('Error updating request status:', error);
        }
    };

    if (loading) {
        return <div className="text-center py-4">Loading exchange details...</div>;
    }

    if (error) {
        return <div className="text-red-500 text-center py-4">Error: {error}</div>;
    }

    if (!exchange) {
        return <div className="text-center text-gray-600 py-4">Exchange request not found or you are not authorized to view it.</div>;
    }

    const isRequester = exchange.requester._id === user._id;
    const isOwner = exchange.owner._id === user._id;
    const otherParty = isRequester ? exchange.owner : exchange.requester;

    const getStatusColor = (status) => {
        switch (status) {
            case 'Accepted': return 'text-green-600';
            case 'Declined': return 'text-red-600';
            case 'Completed': return 'text-blue-600';
            case 'Cancelled': return 'text-yellow-600';
            default: return 'text-orange-600';
        }
    };

    return (
        <div className="max-w-3xl mx-auto p-6 bg-white rounded-lg shadow-md mt-10">
            <h1 className="text-3xl font-bold mb-6 text-center">Exchange Request Details</h1>

            <div className="mb-6 p-4 border border-gray-200 rounded-lg bg-gray-50">
                <p className="mb-2"><strong>Status:</strong> <span className={`font-bold ${getStatusColor(exchange.status)}`}>{exchange.status}</span></p>
                <p className="mb-1"><strong>Requested Book:</strong> <span className="font-semibold">"{exchange.requestedBook.title}"</span> by {exchange.requestedBook.author} (Owner: {exchange.owner.username})</p>
                {exchange.offeredBook && (
                    <p className="mb-1"><strong>Offered Book:</strong> <span className="font-semibold">"{exchange.offeredBook.title}"</span> by {exchange.offeredBook.author} (Owner: {exchange.requester.username})</p>
                )}
                <p className="mb-1"><strong>Initial Message:</strong> {exchange.requestMessage || 'No message provided.'}</p>
                <p className="mb-1"><strong>Initiated by:</strong> {exchange.requester.username}</p>
                <p className="text-sm text-gray-600">On: {new Date(exchange.createdAt).toLocaleDateString()}</p>
                {exchange.status === 'Completed' && exchange.completedAt && (
                    <p className="text-sm text-green-600 mt-2">Completed on: {new Date(exchange.completedAt).toLocaleDateString()}.</p>
                )}
            </div>

            {/* Status Update Actions */}
            <div className="mb-6 p-4 border-t border-gray-200 pt-4 flex flex-wrap gap-2 justify-center">
                {exchange.status === 'Pending' && isOwner && (
                    <>
                        <button onClick={() => updateRequestStatus('Accepted')} className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline transition duration-150 ease-in-out">Accept Request</button>
                        <button onClick={() => updateRequestStatus('Declined')} className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline transition duration-150 ease-in-out">Decline Request</button>
                    </>
                )}
                {(exchange.status === 'Pending' || exchange.status === 'Accepted') && (isRequester || isOwner) && (
                    <button onClick={() => updateRequestStatus('Cancelled')} className="bg-yellow-500 hover:bg-yellow-600 text-black font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline transition duration-150 ease-in-out">Cancel Request</button>
                )}
                {exchange.status === 'Accepted' && (isRequester || isOwner) && (
                    <button onClick={() => updateRequestStatus('Completed')} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline transition duration-150 ease-in-out">Mark as Completed</button>
                )}
            </div>

            {/* Messaging Section - Only visible if exchange is Accepted or Pending */}
            {(exchange.status === 'Pending' || exchange.status === 'Accepted') && (
                <div className="mt-8 border-t border-gray-200 pt-6">
                    <h3 className="text-2xl font-semibold mb-4 text-center">Messages with {otherParty.username}</h3>
                    <div className="max-h-80 overflow-y-auto border border-gray-300 p-4 rounded-lg bg-gray-50 mb-4 flex flex-col space-y-3">
                        {messages.length === 0 ? (
                            <p className="text-center text-gray-600">No messages yet for this exchange.</p>
                        ) : (
                            messages.map((msg) => (
                                <div key={msg._id} className={`p-3 rounded-lg max-w-[70%] ${msg.sender._id === user._id ? 'bg-blue-100 self-end text-right' : 'bg-gray-100 self-start text-left'}`}>
                                    <p className="font-semibold text-gray-800">{msg.sender._id === user._id ? 'You' : msg.sender.username}</p>
                                    <p className="text-gray-700">{msg.content}</p>
                                    <div className="text-xs text-gray-500 mt-1">
                                        {new Date(msg.createdAt).toLocaleString()} {msg.isRead && msg.sender._id === user._id && '(Read)'}
                                    </div>
                                </div>
                            ))
                        )}
                        <div ref={messagesEndRef} />
                    </div>
                    <form onSubmit={handleSendMessage} className="flex gap-2">
                        <textarea
                            value={newMessageContent}
                            onChange={(e) => setNewMessageContent(e.target.value)}
                            rows="2"
                            placeholder="Type your message here..."
                            className="flex-grow p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                        ></textarea>
                        <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg transition duration-150 ease-in-out">Send</button>
                    </form>
                </div>
            )}
        </div>
    );
}

export default ExchangeDetailsPage;
