// client/src/components/Header.js
import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

function Header() {
    const { user, isAuthenticated, logout } = useAuth();
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    return (
        <header className="bg-gray-800 text-white p-4 flex justify-between items-center shadow-md">
            <nav className="flex items-center space-x-4">
                <Link to="/" className="text-white hover:text-gray-300">Home</Link>
                <Link to="/books" className="text-white hover:text-gray-300">Browse Books</Link>
                {isAuthenticated && (
                    <>
                        <Link to="/books/add" className="text-white hover:text-gray-300">Add Book</Link>
                        <Link to="/my-books" className="text-white hover:text-gray-300">My Books</Link>
                        <Link to="/exchanges" className="text-white hover:text-gray-300">My Exchanges</Link>
                    </>
                )}
            </nav>
            <div>
                {isAuthenticated ? (
                    <div className="flex items-center space-x-4">
                        <span className="text-white">Welcome, {user.username}!</span>
                        <button
                            onClick={handleLogout}
                            className="bg-red-600 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                        >
                            Logout
                        </button>
                    </div>
                ) : (
                    <div className="flex items-center space-x-4">
                        <Link to="/register" className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">Register</Link>
                        <Link to="/login" className="bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">Login</Link>
                    </div>
                )}
            </div>
        </header>
    );
}

export default Header;
