// frontend/src/components/Header.js
import React, { useState, useEffect, useCallback } from 'react'; // Import useEffect and useState
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useSocket } from '../contexts/SocketContext'; // <--- Import useSocket
import axios from 'axios'; // Import axios for fetching unread count
import './Header.css'; // Assuming you have this for Header styles

function Header() {
    const { user, isAuthenticated, logout, token } = useAuth(); // Get token from useAuth
    const { socket, isConnected } = useSocket(); // Get socket and connection status
    const navigate = useNavigate();
    const [unreadCount, setUnreadCount] = useState(0); // State for unread count

    // Function to fetch unread notification count
    const fetchUnreadCount = useCallback(async () => {
        if (!token || !isAuthenticated) {
            setUnreadCount(0);
            return;
        }
        try {
            const config = {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            };
            const { data } = await axios.get('/api/notifications/unread-count', config);
            setUnreadCount(data.count);
        } catch (error) {
            console.error('Error fetching unread notification count:', error);
            // Optionally, toast.error('Failed to fetch unread notifications');
        }
    }, [token, isAuthenticated]);

    useEffect(() => {
        // Fetch count on component mount
        fetchUnreadCount();

        // Listen for newNotification events from socket to update count
        if (socket) {
            const handleNewNotification = () => {
                // When a new notification arrives, re-fetch the count
                fetchUnreadCount();
            };
            socket.on('newNotification', handleNewNotification);

            // Clean up the event listener
            return () => {
                socket.off('newNotification', handleNewNotification);
            };
        }
    }, [socket, fetchUnreadCount]); // Re-run if socket or fetchUnreadCount changes

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    return (
        <header className="header">
            <nav>
                <Link to="/">Home</Link>
                <Link to="/books">Browse Books</Link>
                {isAuthenticated && (
                    <>
                        <Link to="/books/add">Add Book</Link>
                        <Link to="/my-books">My Books</Link>
                        <Link to="/exchanges">My Exchanges</Link>
                        <Link to="/profile">My Profile</Link>
                        <Link to="/notifications" className="notifications-link"> {/* <--- NEW LINK */}
                            Notifications
                            {unreadCount > 0 && <span className="notification-badge">{unreadCount}</span>}
                        </Link>
                    </>
                )}
            </nav>
            <div className="header-auth-section">
                {isAuthenticated ? (
                    <>
                        <span>Welcome, {user.username}!</span>
                        <button
                            onClick={handleLogout}
                            className="header-logout-btn"
                        >
                            Logout
                        </button>
                    </>
                ) : (
                    <div className="header-login-register">
                        <Link to="/register" className="register-link">Register</Link>
                        <Link to="/login" className="login-link">Login</Link>
                    </div>
                )}
            </div>
        </header>
    );
}

export default Header;
