// client/src/pages/AddBookPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

function AddBookPage() {
    const { token } = useAuth();
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        title: '',
        author: '',
        genre: '',
        isbn: '',
        condition: 'Good',
        description: '',
        imageUrl: '',
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
            };
            // Ensure ISBN is null if empty string to work with sparse index
            const dataToSend = { ...formData };
            if (dataToSend.isbn === '') {
                dataToSend.isbn = null;
            }
            if (dataToSend.imageUrl === '') {
                dataToSend.imageUrl = 'https://via.placeholder.com/150'; // Ensure default if empty
            }


            await axios.post('/api/books', dataToSend, config); // Use dataToSend
            toast.success('Book added successfully!');
            navigate('/my-books');
        } catch (error) {
            const errorMessage = error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
            toast.error(`Failed to add book: ${errorMessage}`);
            console.error('Error adding book:', error);
        }
    };

    return (
        <div style={{
            maxWidth: '600px', // max-w-xl (approx)
            margin: '40px auto', // mx-auto mt-10
            padding: '20px', // p-6
            backgroundColor: 'white', // bg-white
            borderRadius: '8px', // rounded-lg
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)' // shadow-md
        }}>
            <h1 style={{
                fontSize: '2em', // text-3xl
                fontWeight: 'bold', // font-bold
                marginBottom: '24px', // mb-6
                textAlign: 'center' // text-center
            }}>Add a New Book</h1>
            <form onSubmit={handleSubmit} style={{
                display: 'flex', // flex
                flexDirection: 'column', // flex-col (for space-y-4 behavior)
                gap: '16px' // space-y-4 (approx)
            }}>
                <div>
                    <label htmlFor="title" style={{
                        display: 'block', // block
                        color: '#4a5568', // text-gray-700
                        fontSize: '0.875em', // text-sm
                        fontWeight: 'bold', // font-bold
                        marginBottom: '8px' // mb-2
                    }}>Title:</label>
                    <input
                        type="text"
                        id="title"
                        name="title"
                        value={formData.title}
                        onChange={handleChange}
                        className="form-input" // Rely on index.css for general input styling
                        required
                    />
                </div>
                <div>
                    <label htmlFor="author" style={{ display: 'block', color: '#4a5568', fontSize: '0.875em', fontWeight: 'bold', marginBottom: '8px' }}>Author:</label>
                    <input
                        type="text"
                        id="author"
                        name="author"
                        value={formData.author}
                        onChange={handleChange}
                        className="form-input"
                        required
                    />
                </div>
                <div>
                    <label htmlFor="genre" style={{ display: 'block', color: '#4a5568', fontSize: '0.875em', fontWeight: 'bold', marginBottom: '8px' }}>Genre:</label>
                    <input
                        type="text"
                        id="genre"
                        name="genre"
                        value={formData.genre}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <div>
                    <label htmlFor="isbn" style={{ display: 'block', color: '#4a5568', fontSize: '0.875em', fontWeight: 'bold', marginBottom: '8px' }}>ISBN (Optional):</label>
                    <input
                        type="text"
                        id="isbn"
                        name="isbn"
                        value={formData.isbn}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <div>
                    <label htmlFor="condition" style={{ display: 'block', color: '#4a5568', fontSize: '0.875em', fontWeight: 'bold', marginBottom: '8px' }}>Condition:</label>
                    <select
                        id="condition"
                        name="condition"
                        value={formData.condition}
                        onChange={handleChange}
                        className="form-input" // Apply general input styling
                        required
                    >
                        <option value="New">New</option>
                        <option value="Like New">Like New</option>
                        <option value="Good">Good</option>
                        <option value="Fair">Fair</option>
                        <option value="Worn">Worn</option>
                    </select>
                </div>
                <div>
                    <label htmlFor="description" style={{ display: 'block', color: '#4a5568', fontSize: '0.875em', fontWeight: 'bold', marginBottom: '8px' }}>Description:</label>
                    <textarea
                        id="description"
                        name="description"
                        value={formData.description}
                        onChange={handleChange}
                        rows="4"
                        className="form-input" // Apply general input styling
                    ></textarea>
                </div>
                <div>
                    <label htmlFor="imageUrl" style={{ display: 'block', color: '#4a5568', fontSize: '0.875em', fontWeight: 'bold', marginBottom: '8px' }}>Image URL (Optional):</label>
                    <input
                        type="text"
                        id="imageUrl"
                        name="imageUrl"
                        value={formData.imageUrl}
                        onChange={handleChange}
                        placeholder="e.g., https://example.com/book-cover.jpg"
                        className="form-input" // Apply general input styling
                    />
                </div>
                <button
                    type="submit"
                    className="form-button" // Rely on index.css for button styling
                >
                    Add Book
                </button>
            </form>
        </div>
    );
}

export default AddBookPage;
