// frontend/src/pages/ProfilePage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';
import '../components/Form.css'; // For form styles
import '../App.css'; // For page-heading, status-message

function ProfilePage() {
    const { token, updateUserInContext } = useAuth(); // Destructure updateUserInContext
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        firstName: '',
        lastName: '',
        address: '',
        city: '',
        state: '',
        zipCode: '',
        profilePictureUrl: '', // This will hold the URL for display
        password: '',
        confirmPassword: ''
    });
    const [profileImageFile, setProfileImageFile] = useState(null); // State for the image file itself
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUserProfile = async () => {
            if (!token) {
                setLoading(false);
                setError('Authentication token not found. Please log in.');
                return;
            }
            try {
                const config = {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                };
                const { data } = await axios.get('/api/auth/profile', config);
                setFormData({
                    username: data.username || '',
                    email: data.email || '',
                    firstName: data.firstName || '',
                    lastName: data.lastName || '',
                    address: data.address || '',
                    city: data.city || '',
                    state: data.state || '',
                    zipCode: data.zipCode || '',
                    profilePictureUrl: data.profilePictureUrl || 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTwM_ABMwdQL7Zb4rLmaMl964QE9D4J20TA3w&s',
                    password: '',
                    confirmPassword: ''
                });
                setLoading(false);
            } catch (err) {
                console.error('Error fetching profile:', err);
                const errorMessage = err.response && err.response.data.message
                    ? err.response.data.message
                    : err.message;
                setError(`Failed to load profile: ${errorMessage}`);
                toast.error(`Failed to load profile: ${errorMessage}`);
                setLoading(false);
            }
        };
        fetchUserProfile();
    }, [token]); // Dependency on token to re-fetch if login/logout changes token

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleFileChange = (e) => {
        setProfileImageFile(e.target.files[0]); // Store the actual file
        // Create a local URL for instant preview (won't be uploaded)
        if (e.target.files[0]) {
            setFormData(prev => ({
                ...prev,
                profilePictureUrl: URL.createObjectURL(e.target.files[0])
            }));
        } else {
            // If file is cleared, revert preview to stored URL or default placeholder
            setFormData(prev => ({
                ...prev,
                profilePictureUrl: prev.profilePictureUrl || 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTwM_ABMwdQL7Zb4rLmaMl964QE9D4J20TA3w&s'
            }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (formData.password && formData.password !== formData.confirmPassword) {
            toast.error('New passwords do not match!');
            return;
        }

        // Create FormData object to send both text fields and the file
        const dataToUpdate = new FormData();
        // Append all text fields
        dataToUpdate.append('username', formData.username);
        dataToUpdate.append('email', formData.email);
        dataToUpdate.append('firstName', formData.firstName);
        dataToUpdate.append('lastName', formData.lastName);
        dataToUpdate.append('address', formData.address);
        dataToUpdate.append('city', formData.city);
        dataToUpdate.append('state', formData.state);
        dataToUpdate.append('zipCode', formData.zipCode);

        // Append image file if selected
        if (profileImageFile) {
            dataToUpdate.append('profileImage', profileImageFile); // 'profileImage' must match the field name in upload.single() on backend
        } else {
            // If no new file, explicitly send the current profilePictureUrl (even if it's the placeholder or empty string)
            // This tells the backend whether to retain the old one or set a default/clear.
            dataToUpdate.append('profilePictureUrl', formData.profilePictureUrl);
        }

        // Append password if provided
        if (formData.password) {
            dataToUpdate.append('password', formData.password);
        }

        try {
            const config = {
                headers: {
                    'Content-Type': 'multipart/form-data', // Crucial for sending files
                    Authorization: `Bearer ${token}`,
                },
            };

            const { data } = await axios.put('/api/auth/profile', dataToUpdate, config);
            toast.success('Profile updated successfully!');

            // Update user in AuthContext to reflect new data (especially username/profile pic)
            // This ensures the header and other parts of the app update immediately
            updateUserInContext(data);

            // Clear password fields after update
            setFormData(prev => ({ ...prev, password: '', confirmPassword: '' }));

        } catch (error) {
            console.error('Error updating profile:', error);
            const errorMessage = error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
            toast.error(`Failed to update profile: ${errorMessage}`);
        }
    };

    if (loading) {
        return <div className="status-message">Loading profile...</div>;
    }

    if (error) {
        return <div className="status-message error-message">Error: {error}</div>;
    }

    return (
        <div className="form-container">
            <h1 className="page-heading">My Profile</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group" style={{ textAlign: 'center', marginBottom: '20px' }}>
                    <img
                        src={formData.profilePictureUrl}
                        alt="Profile"
                        style={{ width: '100px', height: '100px', borderRadius: '50%', objectFit: 'cover', border: '2px solid #ccc' }}
                        onError={(e) => { e.target.onerror = null; e.target.src = 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTwM_ABMwdQL7Zb4rLmaMl964QE9D4J20TA3w&s'; }}
                    />
                    <label htmlFor="profileImageFile" style={{ display: 'block', marginTop: '10px', fontSize: '0.9em', color: '#555' }}>Upload New Profile Picture (Optional):</label>
                    <input
                        type="file"
                        id="profileImageFile"
                        name="profileImageFile"
                        onChange={handleFileChange}
                        accept="image/*" // Restrict to image files
                        className="form-input"
                        style={{ height: 'auto', padding: '8px' }} // Adjust styling for file input
                    />
                    <label htmlFor="profilePictureUrlText" style={{ display: 'block', marginTop: '10px', fontSize: '0.9em', color: '#555' }}>Or provide Image URL:</label>
                    <input
                        type="text"
                        id="profilePictureUrlText"
                        name="profilePictureUrl"
                        value={formData.profilePictureUrl}
                        onChange={handleChange}
                        className="form-input"
                        placeholder="e.g., https://example.com/your-pic.jpg"
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="username">Username:</label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        value={formData.username}
                        onChange={handleChange}
                        className="form-input"
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email Address:</label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        className="form-input"
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="firstName">First Name:</label>
                    <input
                        type="text"
                        id="firstName"
                        name="firstName"
                        value={formData.firstName}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="lastName">Last Name:</label>
                    <input
                        type="text"
                        id="lastName"
                        name="lastName"
                        value={formData.lastName}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="address">Address:</label>
                    <input
                        type="text"
                        id="address"
                        name="address"
                        value={formData.address}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="city">City:</label>
                    <input
                        type="text"
                        id="city"
                        name="city"
                        value={formData.city}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="state">State:</label>
                    <input
                        type="text"
                        id="state"
                        name="state"
                        value={formData.state}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="zipCode">Zip Code:</label>
                    <input
                        type="text"
                        id="zipCode"
                        name="zipCode"
                        value={formData.zipCode}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <hr style={{ margin: '30px 0', borderColor: '#eee' }} />
                <h2 style={{ fontSize: '1.2em', fontWeight: 'bold', marginBottom: '15px', color: '#555' }}>Change Password (Optional)</h2>
                <div className="form-group">
                    <label htmlFor="password">New Password:</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        className="form-input"
                        placeholder="Enter new password (min 6 characters)"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="confirmPassword">Confirm New Password:</label>
                    <input
                        type="password"
                        id="confirmPassword"
                        name="confirmPassword"
                        value={formData.confirmPassword}
                        onChange={handleChange}
                        className="form-input"
                        placeholder="Confirm new password"
                    />
                </div>
                <button type="submit" className="form-button">Update Profile</button>
            </form>
        </div>
    );
}

export default ProfilePage;
