// client/src/pages/RequestExchangePage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useNavigate, useLocation, Link } from 'react-router-dom'; // For query params
import { useAuth } from '../contexts/AuthContext';

function RequestExchangePage() {
    const { token, user } = useAuth();
    const navigate = useNavigate();
    const location = useLocation();

    const [requestedBookId, setRequestedBookId] = useState('');
    const [requestedBookTitle, setRequestedBookTitle] = useState('');
    const [ownerId, setOwnerId] = useState('');

    const [myBooks, setMyBooks] = useState([]);
    const [offeredBookId, setOfferedBookId] = useState('');
    const [requestMessage, setRequestMessage] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const queryParams = new URLSearchParams(location.search);
        const id = queryParams.get('requestedBookId');
        const title = queryParams.get('requestedBookTitle');
        const owner = queryParams.get('ownerId');

        if (id && title && owner) {
            setRequestedBookId(id);
            setRequestedBookTitle(decodeURIComponent(title));
            setOwnerId(owner);
        } else {
            toast.error('Missing book information for exchange request.');
            navigate('/books');
            return;
        }

        const fetchMyBooks = async () => {
            try {
                const config = { headers: { Authorization: `Bearer ${token}` } };
                const { data } = await axios.get('/api/books/mybooks', config);
                setMyBooks(data.filter(book => book.availabilityStatus === 'Available'));
                setLoading(false);
            } catch (err) {
                console.error('Error fetching my books:', err);
                setError('Failed to load your books for offering.');
                toast.error('Failed to load your books.');
                setLoading(false);
            }
        };

        if (token) {
            fetchMyBooks();
        } else {
            setLoading(false);
            toast.warn('Please log in to proceed with the exchange request.');
            navigate('/login');
        }
    }, [location.search, navigate, token]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const requestData = {
                requestedBookId: requestedBookId,
                offeredBookId: offeredBookId || null,
                requestMessage: requestMessage,
            };
            await axios.post('/api/exchanges', requestData, config);
            toast.success('Exchange request sent successfully!');
            navigate('/exchanges');
        } catch (error) {
            const errorMessage = error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
            toast.error(`Failed to send request: ${errorMessage}`);
            console.error('Error sending exchange request:', error);
        }
    };

    if (loading) {
        return <div className="text-center py-4">Loading your books...</div>;
    }

    if (error) {
        return <div className="text-red-500 text-center py-4">Error: {error}</div>;
    }

    return (
        <div className="max-w-md mx-auto p-6 bg-white rounded-lg shadow-md mt-10">
            <h1 className="text-2xl font-bold mb-6 text-center">Request Exchange for: "{requestedBookTitle}"</h1>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label htmlFor="offeredBook" className="block text-gray-700 text-sm font-bold mb-2">Offer your book (Optional):</label>
                    <select
                        id="offeredBook"
                        name="offeredBook"
                        value={offeredBookId}
                        onChange={(e) => setOfferedBookId(e.target.value)}
                        className="shadow border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    >
                        <option value="">-- Select a book to offer --</option>
                        {myBooks.map((book) => (
                            <option key={book._id} value={book._id}>
                                {book.title} by {book.author} ({book.condition})
                            </option>
                        ))}
                    </select>
                    {myBooks.length === 0 && <p className="text-sm text-gray-600 mt-2">You don't have any available books to offer. <Link to="/books/add" className="text-blue-500 hover:underline">Add one now!</Link></p>}
                </div>
                <div>
                    <label htmlFor="requestMessage" className="block text-gray-700 text-sm font-bold mb-2">Your Message:</label>
                    <textarea
                        id="requestMessage"
                        name="requestMessage"
                        value={requestMessage}
                        onChange={(e) => setRequestMessage(e.target.value)}
                        rows="5"
                        placeholder="Tell the owner why you're interested in their book and suggest meetup/shipping details."
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        required
                    ></textarea>
                </div>
                <button
                    type="submit"
                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-full transition duration-150 ease-in-out"
                >
                    Send Request
                </button>
            </form>
        </div>
    );
}

export default RequestExchangePage;
