// client/src/pages/BookListPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext'; // Import useAuth to get user info
import { useNavigate } from 'react-router-dom'; // For navigation

function BookListPage() {
    const { user, isAuthenticated } = useAuth(); // Get logged-in user and auth status
    const navigate = useNavigate();

    const [books, setBooks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchBooks = async () => {
            try {
                setLoading(true);
                const { data } = await axios.get('/api/books');
                setBooks(data);
                setLoading(false);
            } catch (err) {
                console.error('Error fetching books:', err);
                setError('Failed to load books. Please try again later.');
                toast.error('Failed to load books.');
                setLoading(false);
            }
        };

        fetchBooks();
    }, []);

    const handleRequestExchange = (requestedBookId, ownerId, requestedBookTitle) => {
        if (!isAuthenticated) {
            toast.warn('Please log in to request an exchange.');
            navigate('/login');
            return;
        }
        if (user._id === ownerId) {
            toast.info('You cannot request an exchange for your own book.');
            return;
        }
        // Navigate to a new page to complete the request
        // We'll create this page: /request-exchange
        navigate(`/request-exchange?requestedBookId=${requestedBookId}&ownerId=${ownerId}&requestedBookTitle=${encodeURIComponent(requestedBookTitle)}`);
    };


    if (loading) {
        return <div>Loading books...</div>;
    }

    if (error) {
        return <div style={{ color: 'red' }}>Error: {error}</div>;
    }

    return (
        <div>
            <h1>Available Books</h1>
            {books.length === 0 ? (
                <p>No books are available for exchange at the moment. Be the first to add one!</p>
            ) : (
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '20px' }}>
                    {books.map((book) => (
                        <div key={book._id} style={{ border: '1px solid #ddd', padding: '15px', borderRadius: '8px', boxShadow: '2px 2px 8px rgba(0,0,0,0.1)' }}>
                            <img src={book.imageUrl} alt={book.title} style={{ width: '100%', height: '200px', objectFit: 'cover', borderRadius: '4px', marginBottom: '10px' }} />
                            <h3>{book.title}</h3>
                            <p><strong>Author:</strong> {book.author}</p>
                            <p><strong>Genre:</strong> {book.genre || 'N/A'}</p>
                            <p><strong>Condition:</strong> {book.condition}</p>
                            <p><strong>Owner:</strong> {book.owner ? book.owner.username : 'Unknown'}</p>
                            <p style={{ fontSize: '0.9em', color: '#666' }}>{book.description.substring(0, 100)}{book.description.length > 100 ? '...' : ''}</p>
                            {isAuthenticated && user && book.owner && user._id !== book.owner._id && (
                                <button
                                    onClick={() => handleRequestExchange(book._id, book.owner._id, book.title)}
                                    style={{ marginTop: '10px', padding: '8px 15px', background: '#007bff', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
                                >
                                    Request Exchange
                                </button>
                            )}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default BookListPage;
