// client/src/pages/RegisterPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { toast } from 'react-toastify'; // Ensure toast is imported

function RegisterPage() {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const navigate = useNavigate();
    const { register } = useAuth();

    const submitHandler = async (e) => {
        e.preventDefault();
        if (password !== confirmPassword) {
            toast.error('Passwords do not match!');
            return;
        }
        const success = await register(username, email, password);
        if (success) {
            navigate('/');
        }
    };

    return (
        <div style={{
            maxWidth: '480px', // max-w-md (approx)
            margin: '40px auto', // mx-auto mt-10
            padding: '24px', // p-6
            backgroundColor: 'white', // bg-white
            borderRadius: '8px', // rounded-lg
            boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px rgba(0, 0, 0, 0.05)' // shadow-md
        }}>
            <h2 style={{
                fontSize: '1.5em', // text-2xl
                fontWeight: 'bold', // font-bold
                marginBottom: '24px', // mb-6
                textAlign: 'center' // text-center
            }}>Register</h2>
            <form onSubmit={submitHandler} style={{
                display: 'flex', // flex
                flexDirection: 'column', // flex-col (for space-y-4 behavior)
                gap: '16px' // space-y-4 (approx)
            }}>
                <div>
                    <label htmlFor="username" style={{
                        display: 'block', // block
                        color: '#4a5568', // text-gray-700
                        fontSize: '0.875em', // text-sm
                        fontWeight: 'bold', // font-bold
                        marginBottom: '8px' // mb-2
                    }}>Username:</label>
                    <input
                        type="text"
                        id="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        className="form-input" // Use global form-input class
                        required
                    />
                </div>
                <div>
                    <label htmlFor="email" style={{ display: 'block', color: '#4a5568', fontSize: '0.875em', fontWeight: 'bold', marginBottom: '8px' }}>Email Address:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        className="form-input" // Use global form-input class
                        required
                    />
                </div>
                <div>
                    <label htmlFor="password" style={{ display: 'block', color: '#4a5568', fontSize: '0.875em', fontWeight: 'bold', marginBottom: '8px' }}>Password:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        className="form-input" // Use global form-input class
                        required
                    />
                </div>
                <div>
                    <label htmlFor="confirmPassword" style={{ display: 'block', color: '#4a5568', fontSize: '0.875em', fontWeight: 'bold', marginBottom: '8px' }}>Confirm Password:</label>
                    <input
                        type="password"
                        id="confirmPassword"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        className="form-input" // Use global form-input class
                        required
                    />
                </div>
                <button
                    type="submit"
                    className="form-button" // Use global form-button class
                    style={{ // Override specific button color and hover
                        backgroundColor: '#007bff', // Reverting to initial standard blue
                        width: '100%' // w-full
                    }}
                    onMouseOver={(e) => e.currentTarget.style.backgroundColor = '#0056b3'} // Darker blue on hover
                    onMouseOut={(e) => e.currentTarget.style.backgroundColor = '#007bff'}
                >
                    Register
                </button>
            </form>
        </div>
    );
}

export default RegisterPage;
