// frontend/src/pages/ExchangeDetailsPage.js
import React, { useEffect, useState, useCallback, useRef } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import './ExchangeDetails.css'; // Import ExchangeDetails.css
import '../App.css'; // Import App.css for page-heading, status-message etc.
import '../components/Form.css'; // Import Form.css for general form-input/button styles

function ExchangeDetailsPage() {
    const { id: exchangeRequestId } = useParams();
    const { token, user } = useAuth();
    const navigate = useNavigate();

    const [exchange, setExchange] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessageContent, setNewMessageContent] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const messagesEndRef = useRef(null);

    const fetchExchangeDetails = useCallback(async () => {
        if (!token || !exchangeRequestId) return;
        try {
            setLoading(true);
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const { data } = await axios.get(`/api/exchanges/${exchangeRequestId}`, config);
            setExchange(data);
            setLoading(false);
        } catch (err) {
            console.error('Error fetching exchange details:', err);
            const errorMessage = err.response && err.response.data.message
                ? err.response.data.message
                : err.message;
            setError(`Failed to load exchange details: ${errorMessage}`);
            toast.error(`Failed to load exchange details: ${errorMessage}`);
            setLoading(false);
            if (err.response && (err.response.status === 403 || err.response.status === 404)) {
                navigate('/exchanges');
            }
        }
    }, [exchangeRequestId, token, navigate]);

    const fetchMessages = useCallback(async () => {
        if (!token || !exchangeRequestId) return;
        try {
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const { data } = await axios.get(`/api/messages/${exchangeRequestId}`, config);
            setMessages(data);
        } catch (err) {
            console.error('Error fetching messages:', err);
            toast.error('Failed to load messages.');
        }
    }, [exchangeRequestId, token]);

    useEffect(() => {
        fetchExchangeDetails();
        fetchMessages();
        const interval = setInterval(fetchMessages, 5000);
        return () => clearInterval(interval);
    }, [fetchExchangeDetails, fetchMessages]);

    useEffect(() => {
        if (messagesEndRef.current) {
            messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    }, [messages]);


    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (!newMessageContent.trim()) {
            toast.warn('Message cannot be empty.');
            return;
        }
        if (!exchange || !user) {
            toast.error('Cannot send message: Exchange or user data missing.');
            return;
        }

        const receiverId = exchange.requester._id === user._id ? exchange.owner._id : exchange.requester._id;

        try {
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const messageData = {
                receiverId,
                exchangeRequestId,
                content: newMessageContent,
            };
            await axios.post('/api/messages', messageData, config);
            setNewMessageContent('');
            toast.success('Message sent!');
            fetchMessages();
        } catch (error) {
            const errorMessage = error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
            toast.error(`Failed to send message: ${errorMessage}`);
            console.error('Error sending message:', error);
        }
    };

    const updateRequestStatus = async (newStatus) => {
        if (!window.confirm(`Are you sure you want to ${newStatus.toLowerCase()} this request?`)) {
            return;
        }
        try {
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const { data } = await axios.put(`/api/exchanges/${exchangeRequestId}/status`, { status: newStatus }, config);
            setExchange(data);
            toast.success(`Request ${newStatus.toLowerCase()} successfully!`);
        } catch (error) {
            const errorMessage = error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
            toast.error(`Failed to update request: ${errorMessage}`);
            console.error('Error updating request status:', error);
        }
    };

    if (loading) {
        return <div className="status-message">Loading exchange details...</div>;
    }

    if (error) {
        return <div className="status-message error-message">Error: {error}</div>;
    }

    if (!exchange) {
        return <div className="status-message info-message">Exchange request not found or you are not authorized to view it.</div>;
    }

    const isRequester = exchange.requester._id === user._id;
    const isOwner = exchange.owner._id === user._id;
    const otherParty = isRequester ? exchange.owner : exchange.requester;

    const getStatusColorClass = (status) => {
        switch (status) {
            case 'Accepted': return 'status-accepted';
            case 'Declined': return 'status-declined';
            case 'Completed': return 'status-completed';
            case 'Cancelled': return 'status-cancelled';
            default: return 'status-pending'; // Pending
        }
    };

    return (
        <div className="exchange-details-container"> {/* Use .exchange-details-container */}
            <h1 className="page-heading">Exchange Request Details</h1>

            <div className="exchange-details-box"> {/* Use .exchange-details-box */}
                <p><strong>Status:</strong> <span className={`status-display-text ${getStatusColorClass(exchange.status)}`}>{exchange.status}</span></p>
                <p><strong>Requested Book:</strong> <span className="font-semibold">"{exchange.requestedBook.title}"</span> by {exchange.requestedBook.author} (Owner: {exchange.owner.username})</p>
                {exchange.offeredBook && (
                    <p><strong>Offered Book:</strong> <span className="font-semibold">"{exchange.offeredBook.title}"</span> by {exchange.offeredBook.author} (Owner: {exchange.requester.username})</p>
                )}
                <p><strong>Initial Message:</strong> {exchange.requestMessage || 'No message provided.'}</p>
                <p><strong>Initiated by:</strong> {exchange.requester.username}</p>
                <p className="request-meta-text">On: {new Date(exchange.createdAt).toLocaleDateString()}</p>
                {exchange.status === 'Completed' && exchange.completedAt && (
                    <p className="completion-message">Completed on: {new Date(exchange.completedAt).toLocaleDateString()}.</p>
                )}
                {exchange.status === 'Declined' && <p className="declined-message">This exchange request was declined.</p>}
                {exchange.status === 'Cancelled' && <p className="cancelled-message">This exchange request was cancelled.</p>}
            </div>

            {/* Status Update Actions */}
            <div className="action-buttons-section"> {/* Use .action-buttons-section */}
                {exchange.status === 'Pending' && isOwner && (
                    <>
                        <button onClick={() => updateRequestStatus('Accepted')} className="action-button accept-button">Accept Request</button>
                        <button onClick={() => updateRequestStatus('Declined')} className="action-button decline-button">Decline Request</button>
                    </>
                )}
                {(exchange.status === 'Pending' || exchange.status === 'Accepted') && (isRequester || isOwner) && (
                    <button onClick={() => updateRequestStatus('Cancelled')} className="action-button cancel-button">Cancel Request</button>
                )}
                {exchange.status === 'Accepted' && (isRequester || isOwner) && (
                    <button onClick={() => updateRequestStatus('Completed')} className="action-button complete-button">Mark as Completed</button>
                )}
            </div>

            {/* Messaging Section - Only visible if exchange is Accepted or Pending */}
            {(exchange.status === 'Pending' || exchange.status === 'Accepted') && (
                <div className="messaging-section"> {/* Use .messaging-section */}
                    <h3 className="section-heading">Messages with {otherParty.username}</h3>
                    <div className="messages-display"> {/* Use .messages-display */}
                        {messages.length === 0 ? (
                            <p className="status-message info-message">No messages yet for this exchange.</p>
                        ) : (
                            messages.map((msg) => (
                                <div key={msg._id} className={`message-bubble ${msg.sender._id === user._id ? 'message-sent' : 'message-received'}`}> {/* Use .message-bubble and dynamic classes */}
                                    <p className="message-sender">{msg.sender._id === user._id ? 'You' : msg.sender.username}</p>
                                    <p className="message-content">{msg.content}</p>
                                    <div className="message-time">
                                        {new Date(msg.createdAt).toLocaleString()} {msg.isRead && msg.sender._id === user._id && '(Read)'}
                                    </div>
                                </div>
                            ))
                        )}
                        <div ref={messagesEndRef} />
                    </div>
                    <form onSubmit={handleSendMessage} className="message-input-form"> {/* Use .message-input-form */}
                        <textarea
                            value={newMessageContent}
                            onChange={(e) => setNewMessageContent(e.target.value)}
                            rows="2"
                            placeholder="Type your message here..."
                            className="form-input message-textarea" /* Reuse .form-input, add .message-textarea */
                        ></textarea>
                        <button type="submit" className="action-button send-message-button">Send</button> {/* Reuse .action-button, add .send-message-button */}
                    </form>
                </div>
            )}
        </div>
    );
}

