// client/src/pages/ExchangeDetailsPage.js
import React, { useEffect, useState, useCallback, useRef } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

function ExchangeDetailsPage() {
    const { id: exchangeRequestId } = useParams(); // Get ID from URL params
    const { token, user } = useAuth();
    const navigate = useNavigate();

    const [exchange, setExchange] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessageContent, setNewMessageContent] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const messagesEndRef = useRef(null); // Ref for auto-scrolling messages

    const fetchExchangeDetails = useCallback(async () => {
        if (!token || !exchangeRequestId) return;
        try {
            setLoading(true);
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const { data } = await axios.get(`/api/exchanges/${exchangeRequestId}`, config);
            setExchange(data);
            setLoading(false);
        } catch (err) {
            console.error('Error fetching exchange details:', err);
            const errorMessage = err.response && err.response.data.message
                ? err.response.data.message
                : err.message;
            setError(`Failed to load exchange details: ${errorMessage}`);
            toast.error(`Failed to load exchange details: ${errorMessage}`);
            setLoading(false);
            // Redirect if not authorized or request not found
            if (err.response && (err.response.status === 403 || err.response.status === 404)) {
                navigate('/exchanges');
            }
        }
    }, [exchangeRequestId, token, navigate]);

    const fetchMessages = useCallback(async () => {
        if (!token || !exchangeRequestId) return;
        try {
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const { data } = await axios.get(`/api/messages/${exchangeRequestId}`, config);
            setMessages(data);
        } catch (err) {
            console.error('Error fetching messages:', err);
            toast.error('Failed to load messages.');
        }
    }, [exchangeRequestId, token]);

    useEffect(() => {
        fetchExchangeDetails();
        fetchMessages();
        // Poll for new messages every few seconds (simple approach)
        const interval = setInterval(fetchMessages, 5000); // Fetch every 5 seconds
        return () => clearInterval(interval); // Clear interval on unmount
    }, [fetchExchangeDetails, fetchMessages]);

    // Auto-scroll to the bottom of messages
    useEffect(() => {
        if (messagesEndRef.current) {
            messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    }, [messages]);


    const handleSendMessage = async (e) => {
        e.preventDefault();
        if (!newMessageContent.trim()) {
            toast.warn('Message cannot be empty.');
            return;
        }
        if (!exchange || !user) {
            toast.error('Cannot send message: Exchange or user data missing.');
            return;
        }

        // Determine the receiver based on who the logged-in user is
        const receiverId = exchange.requester._id === user._id ? exchange.owner._id : exchange.requester._id;

        try {
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const messageData = {
                receiverId,
                exchangeRequestId,
                content: newMessageContent,
            };
            await axios.post('/api/messages', messageData, config);
            setNewMessageContent(''); // Clear input
            toast.success('Message sent!');
            fetchMessages(); // Refresh messages after sending
        } catch (error) {
            const errorMessage = error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
            toast.error(`Failed to send message: ${errorMessage}`);
            console.error('Error sending message:', error);
        }
    };

    const updateRequestStatus = async (newStatus) => {
        if (!window.confirm(`Are you sure you want to ${newStatus.toLowerCase()} this request?`)) {
            return;
        }
        try {
            const config = { headers: { Authorization: `Bearer ${token}` } };
            const { data } = await axios.put(`/api/exchanges/${exchangeRequestId}/status`, { status: newStatus }, config);
            setExchange(data); // Update local state with new status
            toast.success(`Request ${newStatus.toLowerCase()} successfully!`);
            // Optionally navigate back or refresh a main list
            // navigate('/exchanges');
        } catch (error) {
            const errorMessage = error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
            toast.error(`Failed to update request: ${errorMessage}`);
            console.error('Error updating request status:', error);
        }
    };

    if (loading) {
        return <div>Loading exchange details...</div>;
    }

    if (error) {
        return <div style={{ color: 'red' }}>Error: {error}</div>;
    }

    if (!exchange) {
        return <div>Exchange request not found or you are not authorized to view it.</div>;
    }

    // Determine if current user is the requester or the owner
    const isRequester = exchange.requester._id === user._id;
    const isOwner = exchange.owner._id === user._id;
    const otherParty = isRequester ? exchange.owner : exchange.requester;

    return (
        <div style={{ maxWidth: '800px', margin: '0 auto', padding: '20px', border: '1px solid #ddd', borderRadius: '8px', boxShadow: '0 0 10px rgba(0,0,0,0.05)' }}>
            <h1>Exchange Request Details</h1>
            <p><strong>Status:</strong> <span style={{ fontWeight: 'bold', color: exchange.status === 'Accepted' ? 'green' : exchange.status === 'Declined' ? 'red' : exchange.status === 'Completed' ? 'blue' : 'orange' }}>{exchange.status}</span></p>
            <p><strong>Requested Book:</strong> "{exchange.requestedBook.title}" by {exchange.requestedBook.author} (Owner: {exchange.owner.username})</p>
            {exchange.offeredBook && (
                <p><strong>Offered Book:</strong> "{exchange.offeredBook.title}" by {exchange.offeredBook.author} (Owner: {exchange.requester.username})</p>
            )}
            <p><strong>Initial Message:</strong> {exchange.requestMessage || 'No message provided.'}</p>
            <p><strong>Initiated by:</strong> {exchange.requester.username}</p>
            <p><strong>On:</strong> {new Date(exchange.createdAt).toLocaleDateString()}</p>

            {/* Status Update Actions */}
            <div style={{ marginTop: '20px', marginBottom: '20px', borderTop: '1px solid #eee', paddingTop: '20px' }}>
                <h3>Manage Request Status</h3>
                {exchange.status === 'Pending' && isOwner && (
                    <>
                        <button onClick={() => updateRequestStatus('Accepted')} style={{ padding: '8px 15px', background: 'green', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', marginRight: '10px' }}>Accept Request</button>
                        <button onClick={() => updateRequestStatus('Declined')} style={{ padding: '8px 15px', background: 'red', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>Decline Request</button>
                    </>
                )}
                {(exchange.status === 'Pending' || exchange.status === 'Accepted') && (isRequester || isOwner) && (
                    <button onClick={() => updateRequestStatus('Cancelled')} style={{ padding: '8px 15px', background: '#ffc107', color: 'black', border: 'none', borderRadius: '5px', cursor: 'pointer', marginLeft: (exchange.status === 'Pending' && isOwner) ? '10px' : '0' }}>Cancel Request</button>
                )}
                {exchange.status === 'Accepted' && (isRequester || isOwner) && (
                    <button onClick={() => updateRequestStatus('Completed')} style={{ padding: '8px 15px', background: 'blue', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', marginLeft: '10px' }}>Mark as Completed</button>
                )}
                {exchange.status === 'Completed' && <p style={{color: 'green'}}>This exchange has been completed on {new Date(exchange.completedAt).toLocaleDateString()}.</p>}
                {exchange.status === 'Declined' && <p style={{color: 'red'}}>This exchange request was declined.</p>}
                {exchange.status === 'Cancelled' && <p style={{color: '#ffc107'}}>This exchange request was cancelled.</p>}
            </div>

            {/* Messaging Section - Only visible if exchange is Accepted or Pending */}
            {(exchange.status === 'Pending' || exchange.status === 'Accepted') && (
                <div style={{ marginTop: '30px', borderTop: '1px solid #eee', paddingTop: '20px' }}>
                    <h3>Messages with {otherParty.username}</h3>
                    <div style={{ maxHeight: '300px', overflowY: 'auto', border: '1px solid #eee', padding: '10px', borderRadius: '5px', marginBottom: '15px' }}>
                        {messages.length === 0 ? (
                            <p>No messages yet for this exchange.</p>
                        ) : (
                            messages.map((msg) => (
                                <div key={msg._id} style={{ marginBottom: '10px', padding: '8px', borderRadius: '5px', background: msg.sender._id === user._id ? '#e0f7fa' : '#f5f5f5', textAlign: msg.sender._id === user._id ? 'right' : 'left' }}>
                                    <strong>{msg.sender._id === user._id ? 'You' : msg.sender.username}:</strong> {msg.content}
                                    <div style={{ fontSize: '0.8em', color: '#888' }}>
                                        {new Date(msg.createdAt).toLocaleString()} {msg.isRead && msg.sender._id === user._id ? '(Read)' : ''}
                                    </div>
                                </div>
                            ))
                        )}
                        <div ref={messagesEndRef} /> {/* For auto-scrolling */}
                    </div>
                    <form onSubmit={handleSendMessage} style={{ display: 'flex', gap: '10px' }}>
                        <textarea
                            value={newMessageContent}
                            onChange={(e) => setNewMessageContent(e.target.value)}
                            rows="2"
                            placeholder="Type your message here..."
                            style={{ flexGrow: '1', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }}
                        ></textarea>
                        <button type="submit" style={{ padding: '8px 15px', background: '#007bff', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>Send</button>
                    </form>
                </div>
            )}
        </div>
    );
}

export default ExchangeDetailsPage;
