// frontend/src/pages/AddBookPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import '../components/Form.css'; // Assuming you have this for general form styles
import '../App.css'; // For page-heading

function AddBookPage() {
    const { token } = useAuth();
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        title: '',
        author: '',
        genre: '',
        isbn: '',
        condition: 'Good',
        description: '',
    });
    const [imageFile, setImageFile] = useState(null); // State to hold the selected image file

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleFileChange = (e) => {
        setImageFile(e.target.files[0]); // Get the selected file from the input
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Create a FormData object to send both text fields and the file
        const dataToSend = new FormData();
        for (const key in formData) {
            dataToSend.append(key, formData[key]);
        }
        if (imageFile) {
            dataToSend.append('image', imageFile); // 'image' must match the field name in upload.single() on backend
        }

        try {
            const config = {
                headers: {
                    'Content-Type': 'multipart/form-data', // Crucial for sending files
                    Authorization: `Bearer ${token}`,
                },
            };

            await axios.post('/api/books', dataToSend, config);
            toast.success('Book added successfully!');
            navigate('/my-books'); // Redirect to user's own books page
        } catch (error) {
            const errorMessage = error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
            toast.error(`Failed to add book: ${errorMessage}`);
            console.error('Error adding book:', error);
        }
    };

    return (
        <div className="form-container">
            <h1 className="page-heading">Add a New Book</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="title">Title:</label>
                    <input
                        type="text"
                        id="title"
                        name="title"
                        value={formData.title}
                        onChange={handleChange}
                        className="form-input"
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="author">Author:</label>
                    <input
                        type="text"
                        id="author"
                        name="author"
                        value={formData.author}
                        onChange={handleChange}
                        className="form-input"
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="genre">Genre:</label>
                    <input
                        type="text"
                        id="genre"
                        name="genre"
                        value={formData.genre}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="isbn">ISBN (Optional):</label>
                    <input
                        type="text"
                        id="isbn"
                        name="isbn"
                        value={formData.isbn}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="condition">Condition:</label>
                    <select
                        id="condition"
                        name="condition"
                        value={formData.condition}
                        onChange={handleChange}
                        className="form-input form-select"
                        required
                    >
                        <option value="New">New</option>
                        <option value="Like New">Like New</option>
                        <option value="Good">Good</option>
                        <option value="Fair">Fair</option>
                        <option value="Worn">Worn</option>
                    </select>
                </div>
                <div className="form-group">
                    <label htmlFor="description">Description:</label>
                    <textarea
                        id="description"
                        name="description"
                        value={formData.description}
                        onChange={handleChange}
                        rows="4"
                        className="form-input form-textarea"
                    ></textarea>
                </div>
                <div className="form-group">
                    <label htmlFor="imageFile">Upload Book Cover (Optional):</label>
                    <input
                        type="file"
                        id="imageFile"
                        name="imageFile"
                        onChange={handleFileChange}
                        accept="image/*" // Restrict to image files
                        className="form-input"
                        style={{ height: 'auto', padding: '8px' }} // Adjust styling for file input
                    />
                </div>
                <button type="submit" className="form-button">Add Book</button>
            </form>
        </div>
    );
}

export default AddBookPage;
