// frontend/src/pages/ProfilePage.js
import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';
import '../components/Form.css'; // For form styles
import '../App.css'; // For page-heading, status-message

function ProfilePage() {
    const { user, token, logout } = useAuth(); // Get user object from context
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        firstName: '',
        lastName: '',
        address: '',
        city: '',
        state: '',
        zipCode: '',
        profilePictureUrl: '',
        password: '', // For new password input
        confirmPassword: '' // For new password confirmation
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchUserProfile = useCallback(async () => {
        if (!token) {
            setLoading(false);
            setError('Authentication token not found.');
            return;
        }
        try {
            const config = {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            };
            const { data } = await axios.get('/api/auth/profile', config);
            // Populate form data, but don't set password from fetched data
            setFormData({
                username: data.username || '',
                email: data.email || '',
                firstName: data.firstName || '',
                lastName: data.lastName || '',
                address: data.address || '',
                city: data.city || '',
                state: data.state || '',
                zipCode: data.zipCode || '',
                profilePictureUrl: data.profilePictureUrl || 'https://via.placeholder.com/150',
                password: '', // Always keep password fields empty initially
                confirmPassword: ''
            });
            setLoading(false);
        } catch (err) {
            console.error('Error fetching profile:', err);
            const errorMessage = err.response && err.response.data.message
                ? err.response.data.message
                : err.message;
            setError(`Failed to load profile: ${errorMessage}`);
            toast.error(`Failed to load profile: ${errorMessage}`);
            setLoading(false);
        }
    }, [token]);

    useEffect(() => {
        fetchUserProfile();
    }, [fetchUserProfile]);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (formData.password && formData.password !== formData.confirmPassword) {
            toast.error('New passwords do not match!');
            return;
        }

        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
            };

            // Prepare data to send: only send fields that are actually modified or filled
            const dataToUpdate = {
                username: formData.username,
                email: formData.email,
                firstName: formData.firstName,
                lastName: formData.lastName,
                address: formData.address,
                city: formData.city,
                state: formData.state,
                zipCode: formData.zipCode,
                profilePictureUrl: formData.profilePictureUrl,
            };
            if (formData.password) {
                dataToUpdate.password = formData.password;
            }

            const { data } = await axios.put('/api/auth/profile', dataToUpdate, config);
            toast.success('Profile updated successfully!');
            // If email or password changed, might need to update AuthContext user info or re-login
            // For simplicity, we just update the user's name in AuthContext if username changes
            // A more robust solution might re-fetch token or trigger re-login for security.
            if (user && user.username !== data.username) {
                // This is a simplified update. A full update would require refreshing AuthContext.
                // For now, just update local storage and re-parse user to refresh header.
                const userInfo = JSON.parse(localStorage.getItem('userInfo'));
                localStorage.setItem('userInfo', JSON.stringify({ ...userInfo, username: data.username }));
                // To fully update: you might need to call a setUser(updatedUserData) from context
                // or re-fetch current user data for context. For simplicity now, if username changes,
                // a full page reload or proper context refresh is needed for the header to update.
                // For now, let's just make sure the profile page itself updates.
            }

            // Clear password fields after update
            setFormData(prev => ({ ...prev, password: '', confirmPassword: '' }));


        } catch (error) {
            console.error('Error updating profile:', error);
            const errorMessage = error.response && error.response.data.message
                ? error.response.data.message
                : error.message;
            toast.error(`Failed to update profile: ${errorMessage}`);
        }
    };

    if (loading) {
        return <div className="status-message">Loading profile...</div>;
    }

    if (error) {
        return <div className="status-message error-message">Error: {error}</div>;
    }

    return (
        <div className="form-container"> {/* Reusing form container style */}
            <h1 className="page-heading">My Profile</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group" style={{ textAlign: 'center', marginBottom: '20px' }}>
                    <img
                        src={formData.profilePictureUrl}
                        alt="Profile"
                        style={{ width: '100px', height: '100px', borderRadius: '50%', objectFit: 'cover', border: '2px solid #ccc' }}
                        onError={(e) => { e.target.onerror = null; e.target.src = 'https://via.placeholder.com/100'; }}
                    />
                    <label htmlFor="profilePictureUrl" style={{ display: 'block', marginTop: '10px', fontSize: '0.9em', color: '#555' }}>Profile Picture URL:</label>
                    <input
                        type="text"
                        id="profilePictureUrl"
                        name="profilePictureUrl"
                        value={formData.profilePictureUrl}
                        onChange={handleChange}
                        className="form-input"
                        placeholder="e.g., https://example.com/your-pic.jpg"
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="username">Username:</label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        value={formData.username}
                        onChange={handleChange}
                        className="form-input"
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email Address:</label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        className="form-input"
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="firstName">First Name:</label>
                    <input
                        type="text"
                        id="firstName"
                        name="firstName"
                        value={formData.firstName}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="lastName">Last Name:</label>
                    <input
                        type="text"
                        id="lastName"
                        name="lastName"
                        value={formData.lastName}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="address">Address:</label>
                    <input
                        type="text"
                        id="address"
                        name="address"
                        value={formData.address}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="city">City:</label>
                    <input
                        type="text"
                        id="city"
                        name="city"
                        value={formData.city}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="state">State:</label>
                    <input
                        type="text"
                        id="state"
                        name="state"
                        value={formData.state}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="zipCode">Zip Code:</label>
                    <input
                        type="text"
                        id="zipCode"
                        name="zipCode"
                        value={formData.zipCode}
                        onChange={handleChange}
                        className="form-input"
                    />
                </div>
                <hr style={{ margin: '30px 0', borderColor: '#eee' }} />
                <h2 style={{ fontSize: '1.2em', fontWeight: 'bold', marginBottom: '15px', color: '#555' }}>Change Password (Optional)</h2>
                <div className="form-group">
                    <label htmlFor="password">New Password:</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        className="form-input"
                        placeholder="Enter new password (min 6 characters)"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="confirmPassword">Confirm New Password:</label>
                    <input
                        type="password"
                        id="confirmPassword"
                        name="confirmPassword"
                        value={formData.confirmPassword}
                        onChange={handleChange}
                        className="form-input"
                        placeholder="Confirm new password"
                    />
                </div>
                <button type="submit" className="form-button">Update Profile</button>
            </form>
        </div>
    );
}

export default ProfilePage;
