// client/src/App.js
import React from 'react';
import { Routes, Route } from 'react-router-dom'; // Remove BrowserRouter from here
// import { useAuth } from './contexts/AuthContext'; // No longer needed directly here for logout
import HomePage from './pages/HomePages';
import RegisterPage from './pages/RegisterPage';
import LoginPage from './pages/LoginPage';
import BookListPage from './pages/BookListPage';
import AddBookPage from './pages/AddBookPage';
import MyBooksPage from './pages/MyBooksPage';
import ExchangeRequestsPage from './pages/ExchangeRequestsPage';
import ExchangeDetailsPage from './pages/ExchangeDetailsPage';
import ProtectedRoute from './components/ProtectedRoute';
import Header from './components/Header'; // <-- NEW: Import Header
import Footer from './components/Footer'; // <-- NEW: Import Footer
import RequestExchangePage from './pages/RequestExchangePage';

function App() {
    // const { user, logout, isAuthenticated } = useAuth(); // Remove these, Header handles auth UI
    // const navigate = useNavigate(); // REMOVE THIS LINE!

    // const handleLogout = () => { // REMOVE THIS FUNCTION!
    //     logout();
    //     navigate('/login');
    // };

    return (
        <div>
            <Header /> {/* Use the imported Header component */}
            <main style={{ padding: '20px' }}>
                <Routes>
                    <Route path="/" element={<HomePage />} />
                    <Route path="/register" element={<RegisterPage />} />
                    <Route path="/login" element={<LoginPage />} />

                    <Route path="/books" element={<BookListPage />} />

                    <Route
                        path="/books/add"
                        element={
                            <ProtectedRoute>
                                <AddBookPage />
                            </ProtectedRoute>
                        }
                    />
                    <Route
                        path="/my-books"
                        element={
                            <ProtectedRoute>
                                <MyBooksPage />
                            </ProtectedRoute>
                        }
                    />
                    <Route
                        path="/request-exchange" // <-- NEW ROUTE for initiating requests
                        element={ <ProtectedRoute><RequestExchangePage /></ProtectedRoute> }
                    />
                    <Route
                        path="/exchanges"
                        element={
                            <ProtectedRoute>
                                <ExchangeRequestsPage />
                            </ProtectedRoute>
                        }
                    />
                     <Route
                        path="/exchanges/:id"
                        element={
                            <ProtectedRoute>
                                <ExchangeDetailsPage />
                            </ProtectedRoute>
                        }
                    />
                    <Route path="*" element={<h1>404 Not Found</h1>} />
                </Routes>
            </main>
            <Footer /> {/* Use the imported Footer component */}
        </div>
    );
}

export default App;
